import React, { Component } from 'react';
import { Dropdown, Button } from 'react-bootstrap';
import ActualUser from './Actualuser';



class Preferences extends Component {
    constructor(props) {
        super(props);
        this.state = {
            starWarsPersons: [],
            favSpecies: [],
            favAffiliation: [],
            favHomeworld: [],
            favMass: [],

        }
    }

    componentDidMount() {
        this.getPersons();
    }

    /*getFavorites = (e) => {
         this.setState({ [e.target.name]: e.target.value });

     }*/


    getPersons = () => {
        fetch('https://melroune.github.io/starwars-api/api/all.json')
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        starWarsPersons: result,
                        isLoading: false,
                        isError: false,

                    });

                },
                (error) => {
                    this.setState({
                        isLoading: false,
                        isError: true
                    });
                }
            )
    }



    render() {

        return (
            <div>
                <h1>Hello <ActualUser /> choose your preferences</h1>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Species
                        </Dropdown.Toggle>

                        <Dropdown.Menu >

                            <Dropdown.Item name='favSpecies' onSubmit={this.handleChange} value={this.state.value}>
                                <ul>
                                    <li> {[...new Set(this.state.starWarsPersons.map(element => element.species))]}</li>
                                </ul>
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>


                </div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Affiliation
             </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {this.state.starWarsPersons.map((element) =>


                                <Dropdown.Item name='favAffiliation' onSubmit={this.handleChange} >
                                    {(element.affiliations.sort((item) => {
                                        return [item]
                                    }))}
                                </Dropdown.Item>)}
                        </Dropdown.Menu>
                    </Dropdown>


                </div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Home World
             </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {this.state.starWarsPersons.map((element) =>
                                <Dropdown.Item name='favHomeworld' onSubmit={this.handleChange} >{element.homeworld}</Dropdown.Item>)}
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
                <Button variant="primary" type="submit">Submit</Button>
            </div >
        );
    }
}


export default Preferences;
