import React from 'react';
import { Component } from 'react';




class Match extends Component {
    constructor(props) {
        super(props);
        this.state = {
            starWarsPersons:[],


        }
    }

    componentDidMount() {
        this.getPersons();
    }



    getPersons= () => {
        fetch('https://melroune.github.io/starwars-api/api/all.json')
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        starWarsPersons:result,
                        isLoading: false,
                        isError: false,

                    });

                    console.log(this.state)
                },
                (error) => {
                    this.setState({
                        isLoading: false,
                        isError: true
                    });
                }
            )
    }
    render() {
        return (
            <div>
                {this.state.starWarsPersons.map((element) => {
                                return (
                                    <ul>
                                        <li> {(element.name)} </li>
                                        <li> {(element.species)} </li>
                                        <li> {(element.affiliation).forEach((item) => {
                                                return (
                                                    <ul>
                                                        <li>{item}</li>
                                                    </ul>)
                                        }
                                        )
                                        } </li>
                                        <li> {(element.mass)} </li>
                                    </ul>
                                )
                            })}
            </div>
        )
}



}


    export default Match;
