import React from 'react';
import { Component } from 'react';
import ActualUser from './ActualUser.js';

class Match extends Component {
    constructor(props) {
        super(props);
        this.state = {
            starWarsPersons: [],
            matches: [],
            isLoading: true,
            isError: false
        }
    }

    componentDidMount() {
        console.log(window.myPreferences);

        this.getPersons();
    }



    getPersons = () => {
        fetch('https://melroune.github.io/starwars-api/api/all.json')
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        starWarsPersons: result,
                        isLoading: false,
                        isError: false,
                    }, this.findMatches);
                },
                (error) => {
                    this.setState({
                        isLoading: false,
                        isError: true
                    });
                }
            )
    }

    findMatches = () => {
        // Find all matches that fit into the criteria
        // Store it on this.state.matches

        // Mass between 20 and 50
        var filteredResults;
        filteredResults = this.state.starWarsPersons.filter(element => {
            return element.mass > 20 && element.mass < 50;
        });

        console.log(this.state.starWarsPersons.length);
        console.log(filteredResults.length);
    }

    render() {
        return (
            <div>
                <h1>Hello <ActualUser /> you got these options:</h1>
                {this.state.starWarsPersons.map((element) => {
                    return (
                        <ul>
                            <li> name: {(element.name)} </li>
                            <li>species: {(element.species)} </li>

                            <li> {([element.affiliations].map((item) => {
                                return (
                                    <ul>
                                        <li>affiliations: {item}</li>

                                    </ul>)
                            }
                            ))
                            } </li>
                            <li>mass: {(element.mass)}kg </li>
                        </ul>
                    )
                })}


            </div>
        )
    }



}


export default Match;
