import React, { Component } from "react";
import { Dropdown } from 'react-bootstrap'

class Preferences extends Component {
    constructor(props) {
        super(props);
        this.state = {
            starWarsPersons: [],
            favSpecies: [],
            favAffiliation: [],
            favHomeworld: [],
            favMass: [],

        }
    }

    componentDidMount() {
        this.getPersons();
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });

    }


    getPersons = () => {
        fetch('https://melroune.github.io/starwars-api/api/all.json')
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        starWarsPersons: result,
                        isLoading: false,
                        isError: false,

                    });

                    console.log(this.state)
                },
                (error) => {
                    this.setState({
                        isLoading: false,
                        isError: true
                    });
                }
            )
    }





    render() {

        return (
            <div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Species
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item name='favSpecies' onSubmit={this.handleChange} value={this.state.value} >


                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>


                </div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Affiliation
             </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item name='favAffiliation' onSubmit={this.handleChange} >Action</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>


                </div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Home World
             </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item name='favHomeworld' onSubmit={this.handleChange} >Action</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
            </div>
        );
    }
}


export default Preferences;
