import React from 'react';
import { Component } from 'react';
import { Card, CardDeck } from 'react-bootstrap';




class Match extends Component {
    constructor(props) {
        super(props);
        this.state = {
            starWarsPersons: [],


        }
    }

    componentDidMount() {
        this.getPersons();
    }



    getPersons = () => {
        fetch('https://melroune.github.io/starwars-api/api/all.json')
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        starWarsPersons: result,
                        isLoading: false,
                        isError: false,

                    });


                },
                (error) => {
                    this.setState({
                        isLoading: false,
                        isError: true
                    });
                }
            )
    }
    render() {
        return (
            <div>
                {this.state.starWarsPersons.map((element) => {
                    return (
                        <ul>
                            <li> name: {(element.name)} </li>
                            <li>species: {(element.species)} </li>

                            <li> {([element.affiliations].map((item) => {
                                return (
                                    <ul>
                                        <li>affiliations: {item}</li>

                                    </ul>)
                            }
                            ))
                            } </li>
                            <li>mass: {(element.mass)}kg </li>
                        </ul>
                    )
                })}

                <CardDeck>
                    <Card>
                        <Card.Img variant="top" src="holder.js/100px160" />
                        <Card.Body>
                            <Card.Title>Card title</Card.Title>
                            <Card.Text>
                                This is a wider card with supporting text below as a natural lead-in to
                                additional content. This content is a little bit longer.
      </Card.Text>
                        </Card.Body>
                        <Card.Footer>
                            <small className="text-muted">Last updated 3 mins ago</small>
                        </Card.Footer>
                    </Card>
                    <Card>
                        <Card.Img variant="top" src="holder.js/100px160" />
                        <Card.Body>
                            <Card.Title>Card title</Card.Title>
                            <Card.Text>
                                This card has supporting text below as a natural lead-in to additional
        content.{' '}
                            </Card.Text>
                        </Card.Body>
                        <Card.Footer>
                            <small className="text-muted">Last updated 3 mins ago</small>
                        </Card.Footer>
                    </Card>
                    <Card>
                        <Card.Img variant="top" src="holder.js/100px160" />
                        <Card.Body>
                            <Card.Title>Card title</Card.Title>
                            <Card.Text>
                                This is a wider card with supporting text below as a natural lead-in to
                                additional content. This card has even longer content than the first to
                                show that equal height action.
      </Card.Text>
                        </Card.Body>
                        <Card.Footer>
                            <small className="text-muted">Last updated 3 mins ago</small>
                        </Card.Footer>
                    </Card>
                </CardDeck>
            </div>
        )
    }



}


export default Match;
