import React, { Component } from 'react';
import { Dropdown, Button, Container } from 'react-bootstrap';
import ActualUser from './ActualUser.js';



class Preferences extends Component {
    constructor(props) {
        super(props);
        this.state = {
            starWarsPersons: [],
            favSpecies: [],
            favAffiliation: [],
            favHomeworld: [],
            favMass: [],

        }
    }

    componentDidMount() {
        this.getPersons();
    }

    getPersons = () => {
        fetch('https://melroune.github.io/starwars-api/api/all.json')
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        starWarsPersons: result,
                        isLoading: false,
                        isError: false,

                    });

                },
                (error) => {
                    this.setState({
                        isLoading: false,
                        isError: true
                    });
                }
            )
    }

    selectSpecies = (eventKey, event) => {
        console.log(event)
        this.setState({
            favSpecies: eventKey
        });
    }


    render() {

        return (
            <Container>
                <h1>Hello <ActualUser /> choose your preferences</h1>
                <div>
                    <Dropdown onSelect={this.selectSpecies}>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Species
                        </Dropdown.Toggle>
                        <Dropdown.Menu >
                            {[...new Set(this.state.starWarsPersons.map(element => element.species))].sort().map((specie) => (
                                <Dropdown.Item name='favSpecies' eventKey={specie}>{specie}</Dropdown.Item>
                            ))};
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Affiliation
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            {this.state.starWarsPersons.map((element) =>


                                <Dropdown.Item name='favAffiliation' onSubmit={this.handleChange} >
                                    {(element.affiliations.sort((item) => {
                                        return [item]
                                    }))}
                                </Dropdown.Item>)}
                        </Dropdown.Menu>
                    </Dropdown>


                </div>
                <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Favorite Home World
             </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {this.state.starWarsPersons.map((element) =>
                                <Dropdown.Item name='favHomeworld' onSubmit={this.handleChange} >{element.homeworld}</Dropdown.Item>)}
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
                <Button variant="primary" type="submit">Submit</Button>
            </Container>
        );
    }
}


export default Preferences;
